import { createTheme } from '@mui/material/styles';

const theme = createTheme({
  typography: {
    fontFamily: 'Verdana, Arial, sans-serif',
    h1: {
      fontSize: '1.5rem',
    },
    h2: {
      fontSize: '1.2rem',
    },
    h3: {
      fontSize: '1rem',
    },
  },
});

export default theme;
